$(document).ready(function(){var a=document.querySelector(".main__cards");a.querySelectorAll(".main-card.one, .main-card.two, .main-card.three, .main-card.four, .main-card.five").forEach(function(e){e.addEventListener("mouseover",function(){a.classList.add(e.classList[1])}),e.addEventListener("mouseout",function(){a.classList.remove(e.classList[1])})})});